import{_ as e,o as a,c as t,S as r}from"./chunks/framework.54c93748.js";const u=JSON.parse('{"title":"生活的点滴心情","description":"记录生活的点滴心情，阅读到的有趣文字","frontmatter":{"description":"记录生活的点滴心情，阅读到的有趣文字","descriptionHTML":" <span style=\\"color:var(--description-font-color);\\">记录生活的点滴心情，阅读到的有趣文字</span> ","tag":["生活笔记","点滴心情"],"top":false},"headers":[],"relativePath":"life/details/index.md","filePath":"life/details/index.md","lastUpdated":1693013351000}'),o={name:"life/details/index.md"},i=r('<h1 id="生活的点滴心情" tabindex="-1">生活的点滴心情 <a class="header-anchor" href="#生活的点滴心情" aria-label="Permalink to &quot;生活的点滴心情&quot;">​</a></h1><p>记录生活的点滴心情，阅读到的有趣文字和链接</p><h2 id="关于释怀" tabindex="-1">关于释怀 <a class="header-anchor" href="#关于释怀" aria-label="Permalink to &quot;关于释怀&quot;">​</a></h2><p>父亲不到60岁就去世了，还没来得及享受儿孙绕膝，就这么走了，感觉他这辈子就是来给我们还债的，你就这么走了，有太多的不舍，总感觉他没走，我甚至笑多了都感觉对不住老父亲，这几年过得都很压抑。直到一晚，梦里父亲来看我了，我拉着他说了好多话，我舍不得他走啊。父亲告诉我，他这辈子有我们几个儿女，他的功课就完成了，希望看到我们过得开心，不要老惦记他，他要去新的世界过新的生活，看到你们过得好，就像你们希望我在那边过得好一样。过好自己的生活就是对世去亲人最好的安慰，笑起来，开心起来！他在那边看着你的！</p><h2 id="最令你醍醐灌顶的一句话是什么" tabindex="-1">最令你醍醐灌顶的一句话是什么？ <a class="header-anchor" href="#最令你醍醐灌顶的一句话是什么" aria-label="Permalink to &quot;最令你醍醐灌顶的一句话是什么？&quot;">​</a></h2><p>最令你醍醐灌顶的一句话是什么？ - 北柯随笔的回答 - 知乎 <a href="https://www.zhihu.com/question/566420596/answer/3156105564" target="_blank" rel="noreferrer">https://www.zhihu.com/question/566420596/answer/3156105564</a></p>',6),s=[i];function n(d,l,c,h,_,p){return a(),t("div",null,s)}const m=e(o,[["render",n]]);export{u as __pageData,m as default};
